contracts/lotterylmpl.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

// IMPORTS
import {Initializable}      from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {UUPSUpgradeable}    from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

// CONTRACT
contract OmegaLottery is Initializable, UUPSUpgradeable, OwnableUpgradeable
{
    using Strings for uint256;

    // ERRORS
    error InsufficientFunds();
    error InvalidEntryTime();

    error LotteryDNE();
    error LotteryEnded();
    error LotteryNotOpen();
    error LotteryNotStarted();

   // EVENTS
    event LotteryCreated
    (
        uint256 indexed lotteryId,
        uint256 entryFee,
        uint256 startTime,
        uint256 endTime
    );

    event LotteryEntered
    (
        uint256 indexed lotteryId,
        address indexed playerAddress,
        uint256 playerStake
    );

    // TYPES
    enum LotteryStatus 
    {
        NOT_STARTED,
        OPEN,
        CLOSED,
        DRAWING,
        RESOLVED
    }

    struct Lottery 
    {
        uint256 id;
        uint256 entryFee;
        uint256 startTime;
        uint256 endTime;
        uint256 totalPot;
        LotteryStatus status;
        address winner; // empty until lottery resolves
    }

    // STORAGE
    uint256 public lotteryIdCounter;    // incrementing lottery ID. starts @ 1
    mapping(uint256 => Lottery) internal lotteries; // lotteryId => Lottery 
    mapping(uint256 => address[]) internal lotteryPlayers;  // lotteryId => players
    uint256[45] private __gap;  // reserved storage gap for future updates


    // INITIALIZER -> runs exactly once (one-time setup, part of UUPS)
    function initialize(address initialOwner) external initializer 
    {
        __Ownable_init(initialOwner);   // sets contract owner
        __UUPSUpgradeable_init();

        lotteryIdCounter = 1;
    }

    // LOTTERY CREATION
    function createLottery(uint256 entryFee, uint256 startTime, uint256 endTime) external onlyOwner returns (uint256 lotteryId) 
    {
        // enforce rules
        if (startTime >= endTime) revert InvalidEntryTime();

        lotteryId = lotteryIdCounter++;

        Lottery storage lottery = lotteries[lotteryId];
        lottery.id = lotteryId;
        lottery.entryFee = entryFee;
        lottery.startTime = startTime;
        lottery.endTime = endTime;
        lottery.status = LotteryStatus.NOT_STARTED;

        emit LotteryCreated(lotteryId, entryFee, startTime, endTime);
    }

    // JOIN LOTTERY
    function joinLottery(uint256 lotteryId) external payable
    {
        Lottery storage lottery = lotteries[lotteryId];

        // enforce rules
        if (lotteryId == 0) revert LotteryDNE();
        if (block.timestamp < lottery.startTime) revert LotteryNotStarted();
        if (block.timestamp >= lottery.endTime) revert LotteryEnded();
        if (msg.value < lottery.entryFee) revert InsufficientFunds();
        if (lottery.status != LotteryStatus.OPEN) revert LotteryNotOpen();

        // update lottery state
        lotteryPlayers[lotteryId].push(msg.sender);
        lottery.totalPot += msg.value;

        // send event to frontend
        emit LotteryEntered(lotteryId, msg.sender, msg.value);
    }

    // VIEW FUNCTIONS (for debugging/development)
    function getLottery(uint256 lotteryId) external view returns (Lottery memory)
    {
        return lotteries[lotteryId];
    }

    // UUPS
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
}

contracts/randomNumber.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30; // set the version of solidity that we are using

import {VRFConsumerBaseV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/VRFConsumerBaseV2Plus.sol";
import {VRFV2PlusClient} from "@chainlink/contracts/src/v0.8/vrf/dev/libraries/VRFV2PlusClient.sol";

contract randomNumber is VRFConsumerBaseV2Plus {
    event RequestSent(uint256 requestId, uint32 numWords);
    event RequestFulfilled(uint256 requestId, uint256[] randomWords);

    struct RequestStatus 
    {
        bool fulfilled; // whether the request has been successfully fulfilled
        bool exists;    // whether a requestId exists
        uint256[] randomWords;  //   result from VRF
    }

    mapping(uint256 => RequestStatus) public s_requests; // requestId --> requestStatus

    uint256 public s_subscriptionId;    // subscription ID for VRF
    uint256[] public requestIds;        // list of past request IDs
    uint256 public lastRequestId;       // ID of the last request

    // specifies which gas lane to use (which network we are using. in this case, we are using Ethereum Sepolia)
    // check me out: https://docs.chain.link/vrf/v2-5/supported-networks#ethereum-sepolia-testnet
    bytes32 public keyHash = 0x787d74caea10b2b357790d5b5247c2f63d1d91572a9846f780606e4d953677ae;

    // specifies the maximum amount of gas that can be used on a VRF request
    // note: storing each word costs ~20,000 gas, so i've settled on 50k to be safe
    uint32 public callbackGasLimit = 100_000;

    // specifies the amount of words to retrieve per VRF request
    uint32 public numWords = 1;

    // default = 3, can set higher
    uint16 public requestConfirmations = 3;

    constructor(uint256 subscriptionId) VRFConsumerBaseV2Plus(0x9DdfaCa8183c41ad55329BdeeD9F6A8d53168B1B) 
    {
        s_subscriptionId = subscriptionId;
    } 

    function fulfillRandomWords(uint256 _requestId, uint256[] calldata _randomWords) internal override 
    {
        require(s_requests[_requestId].exists, "request not found");

        s_requests[_requestId].fulfilled = true;
        s_requests[_requestId].randomWords = _randomWords;

        emit RequestFulfilled(_requestId, _randomWords);
    }

    function getRequestStatus(uint256 _requestId) external view returns (bool fulfilled, uint256[] memory randomWords)
    {
        require(s_requests[_requestId].exists, "request not found");

        RequestStatus memory request = s_requests[_requestId];
        return (request.fulfilled, request.randomWords);
    }

    function requestRandomWords(bool enableNativePayment) external onlyOwner returns (uint256 requestId)
    {
        // will revert if subscription is not set & funded
        requestId = s_vrfCoordinator.requestRandomWords(
            VRFV2PlusClient.RandomWordsRequest(
            {
                keyHash: keyHash,
                subId: s_subscriptionId,
                requestConfirmations: requestConfirmations,
                callbackGasLimit: callbackGasLimit,
                numWords: numWords,
                extraArgs: VRFV2PlusClient._argsToBytes(VRFV2PlusClient.ExtraArgsV1({nativePayment: enableNativePayment}))
            })
        );

        s_requests[requestId] = RequestStatus({randomWords: new uint256[](0), exists: true, fulfilled: false});
        requestIds.push(requestId);
        lastRequestId = requestId;
        emit RequestSent(requestId, numWords);
        return requestId;
    }
}
